import React, { useState } from 'react';

function UrlInput({ onScrape, loading }) {
  const [urlInput, setUrlInput] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Remove any error message
    setError('');

    // Split and trim URLs
    const urls = urlInput
      .split('\n')
      .map(url => url.trim())
      .filter(url => url !== '');

    // Validate URLs
    if (urls.length === 0) {
      setError('Please enter at least one URL');
      return;
    }

    // Simple URL validation
    const invalidUrls = urls.filter(url => {
      try {
        const parsed = new URL(url);
        return !(['http:', 'https:'].includes(parsed.protocol));
      } catch (e) {
        return true;
      }
    });

    if (invalidUrls.length > 0) {
      setError(`Invalid URL(s): ${invalidUrls.join(', ')}`);
      return;
    }

    // Call the parent handler
    onScrape(urls);
  };

  return (
    <div className="url-input-container">
      <h2>Generate LLMs.txt and Markdown Files</h2>
      <p>Enter one or more URLs (one per line) to scrape and generate files.</p>

      <form onSubmit={handleSubmit}>
        <div className="input-group">
          <label htmlFor="url-input">Website URLs:</label>
          <textarea 
            id="url-input"
            rows="5"
            placeholder="https://example.com"
            value={urlInput}
            onChange={(e) => setUrlInput(e.target.value)}
            required
          />
        </div>

        {error && (
          <div className="alert alert-error">
            {error}
          </div>
        )}

        <button 
          type="submit" 
          className="btn" 
          disabled={loading}
        >
          {loading ? 'Processing...' : 'Generate Files'}
        </button>
      </form>
    </div>
  );
}

export default UrlInput;
